document.addEventListener("DOMContentLoaded",(function(){document.getElementById("header__button").addEventListener("click",(()=>{const e=document.querySelector(".header__burger-menu"),t=document.querySelector(".main");e.classList.contains("header__burger-active")?(e.classList.remove("header__burger-active"),t.classList.remove("main__show-overlay")):(e.classList.add("header__burger-active"),t.classList.add("main__show-overlay"))}))}));